    public boolean hasGroupsSizeX(int[] deck) {
        int[] an = new int[10000];//deck数组长度最长10000，因此定义一个长度为10000的数组记录
        for (int i = 0; i < deck.length; i++) {
            an[deck[i]]++;//通过遍历，记录各数字出现的次数
        }
        int x = an[deck[0]];//确保x不为0，且为an数组中存在的数字
        for (int i = 0; i < 10000; i++) {//遍历数组，用x来记录原x与an[i]的最大公因数
            if (an[i] != 0) x = is(x, an[i]);
        }
        return x >= 2;//如果这个最大公因数大于2，则返回true
    }

    public int is(int a, int b) {//求最大公因数，使用的是欧几里得算法，也叫辗转相除法。
        while (a % b != 0) {
            int c = a % b;
            a = b;
            b = c;
        }
        return b;
    }

