    public int partitionDisjoint(int[] A) {
        // 先找到整个数组最小的数字，这个数字肯定在左边数组
        int min = Arrays.stream(A).min().getAsInt();

        // res是左边数组长度
        // 1）找到最后一个最小数字，加入到左侧数组中
        // 2) 在左侧数组中找到最大的数字
        int res = 0, max = 0, idx = 0;
        for (int i = 0, _max = 0; i < A.length; i++) {
            if (A[i] == min) {
                res = i+1;
                idx = i;
                max = _max;
                break;
            }
            _max = Math.max(_max, A[i]);
        }

        // 右边数组中<最大数字的，那么就要加入子数组
        for (int i = idx+1, _max=0; i < A.length; i++) {
            if (A[i] < max) {
                res = i+1;
                max = Math.max(max, _max);
            }
            _max = Math.max(_max, A[i]);
        }

        return res;
    }

